const config = $items('DB: Get Agent Config')[0].json; 
const agentsRaw = $items('DB: Get Agents').map(item => item.json);
const followUps = $items('DB: Get Follow-ups').map(item => item.json);
const statusJson = $items('Baileys: Check Status')[0].json;
const logs = $items('DB: Get Logs').map(item => item.json);

// Pega os dados do dashboard do nó correto (IF ou seu fallback)
const dashboardNode = $items('IF: Range = this_month').length > 0 ? $items('DB: Get Dashboard Data (This Month)') : $items('DB: Get Dashboard Data (Default)');
const dashboard = dashboardNode[0].json;

const agents = agentsRaw.map(agent => ({
    ...agent,
    available_tools: agent.available_tools || []
}));

// Formata os dados para o frontend
const formattedData = {
  agentConfig: {
    waitTime: config.waitTime,
    notificationTrigger: config.notificationTrigger,
    notificationNumbers: config.notificationNumbers,
    is_active: config.is_active,
    debug_mode: config.debug_mode,
    geminiApiKey: config.gemini_api_key
  },
  agents: agents,
  followUpCadences: followUps,
  logs: logs,
  dashboardData: {
      kpis: {
          totalLeads: Number(dashboard.total_leads) || 0,
          totalCompras: Number(dashboard.total_compras) || 0,
          totalFaturado: Number(dashboard.total_faturado) || 0,
          ticketMedio: Number(dashboard.ticket_medio) || 0,
          taxaConversao: Number(dashboard.taxa_conversao) || 0
      },
      leadsPorDia: [
          { name: 'Seg', leads: 4 },
          { name: 'Ter', leads: 3 },
          { name: 'Qua', leads: 2 },
          { name: 'Qui', leads: 2 },
          { name: 'Sex', leads: 1 },
          { name: 'Sáb', leads: 2 },
          { name: 'Dom', leads: 3 },
      ] // Mockado, mas poderia vir do DB
  },
  isWhatsAppConnected: statusJson.data?.status === 'CONNECTED'
};

return formattedData;
